insert into data_check_types (data_check_type_id, name, entity_type_id, sql, severity_level, fail_threshold)
values (8, 'stores (non-shopping center) with same number of competitions in different trade area thresholds', 2, '
;with non_shopping_centers as (
	select s.store_id
	from stores s
	inner join addresses a on a.address_id = s.address_id
		and a.shopping_center_name is not null
)
, comp_per_trade_area as
(
	select
		cs.home_store_id,
		cs.trade_area_id,
		count(*) as comp_count
	from competitive_stores cs
	inner join trade_areas t on t.trade_area_id = cs.trade_area_id
	where not exists (select 1 from non_shopping_centers where non_shopping_centers.store_id = cs.home_store_id)
	group by cs.home_store_id, cs.trade_area_id
)
, trade_areas_per_store as (
	select t.store_id, count(*) as trade_area_count
	from trade_areas t
	where not exists (select 1 from non_shopping_centers where non_shopping_centers.store_id = t.store_id)
	group by t.store_id
)
, trade_areas_equal_comp as (
	select
		cta.home_store_id,
		count(*) as competitors_per_trade_area
	from comp_per_trade_area cta
	inner join trade_areas_per_store tps on tps.store_id = cta.home_store_id
	group by cta.home_store_id, cta.comp_count, tps.trade_area_count
	having count(*) = tps.trade_area_count
)
, potentially_bad as (select count(*) cnt from trade_areas_equal_comp)
, total_count as (
	select count(*) as cnt
	from trade_areas t
	where not exists (select 1 from non_shopping_centers where non_shopping_centers.store_id = t.store_id)
)
select top 1000 eq.home_store_id as id
    , pb.cnt * 1.0 / tc.cnt as percent_potentially_bad
	, eq.competitors_per_trade_area
from potentially_bad pb
cross join total_count tc
cross join trade_areas_equal_comp eq
', 2, 1);
